double CP154606_Functions::P1546_30(double f,double t,char z,double h1,double d_km,double h2,int ENV,double *Ebfs) 
{
	double E0fs,Eb,corrh2;
	Eb = P1546_20(f,t,z,h1,d_km,&E0fs);
	corrh2 = Correction_h2( f, z, h1, d_km, h2, ENV); 
	Eb = Eb + corrh2;
	Eb = min(Eb,E0fs);

	*Ebfs = E0fs;
	return Eb;
}

double CP154606_Functions::P1546_20(double f,double t,char z,double h1,double d_km,double *Ebfs) 
{
	double E0fs,Eb;
	if ( (f==100.0) || (f==600.0) || (f==2000.0) ) 		Eb = P1546_0(f,t,z,h1,d_km,&E0fs);
	else
	{
		double finf, fsup , Einf,Efsinf,Esup,Efssup;
		if		(f<600.0)	{	finf = 100.0; fsup = 600.0;		}
		else if (f>600.0)	{	finf = 600.0; fsup = 2000.0;	}
 
		Einf = P1546_0(finf,t,z,h1,d_km,&Efsinf);
		Esup = P1546_0(fsup,t,z,h1,d_km,&Efssup);
		Eb = Einf + (Esup-Einf)*log10(f/finf)/log10(fsup/finf);
		E0fs = Efsinf + (Efssup-Efsinf)*log10(f/finf)/log10(fsup/finf); 
	}
	*Ebfs = E0fs;
	return Eb;
}


double CP154606_Functions::Correction_h2(double f,char z,double h1,double d_km,double h2p,int ENV) 
{
	double R = 10.0;
	if ((z==_T('1'))||(z==_T('2'))||(z==_T('3'))||(z==_T('D')))	//Land
		R = 10.0*(ENV==0) + 10.0*(ENV==1) + 10.0*(ENV==2) + 20.0*(ENV==3) + 30.0*(ENV==4);

	double corrh2 = 0.0 , h2 = h2p;
	if (h2!=R)
	{
		double Kh2 = 3.2 + 6.2*log10(f);

		if ((z==_T('1'))||(z==_T('2'))||(z==_T('3'))||(z==_T('D')))	//Land
		{
			double Rp;
			h2 = max(h2,1.0);
			if ( (6.5*d_km + R) > h1 )		Rp = R;
			else							Rp = (1000.0*R*d_km - 15.0*h1)/(1000.0*d_km - 15.0);
			Rp = max(Rp,1.0);

			if ( (ENV==2)||(ENV==3)||(ENV==4) )	//sunurban  ,   urban  ,	??Dense Urban??
			{
				double Knu = 0.0108*sqrt(f);
				if (Rp>h2)
				{
					double pi = 4.0*atan(1.0);
					double hdif = Rp - h2;
					double tetaclut = (180.0/pi)*atan(hdif/27.0);
					double vv = (Knu*sqrt(hdif*tetaclut))-0.1;
					corrh2 = 6.03-(6.9+20.0*log10(vv + sqrt(1.0+vv*vv)));
				}
				else						corrh2 = Kh2*log10(h2/Rp);
			}
			else if ((ENV==0)||(ENV==1))	corrh2 = Kh2*log10(h2/Rp);		//rural  ,  open
		}
		else    // Sea
		{
			if (h2>=10.0)					corrh2 = Kh2*log10(h2/10.0);
			else
			{
				h2 = max(h2,1.0);		//	h2 = max(h2,3.0);
				double d10 = zD06(f,h1,10.0),	dh2 = zD06(f,h1,h2);

				if      (d_km>=d10)	corrh2 = Kh2*log10(h2/10.0);
				else if (d_km<=dh2)	corrh2 = 0.0;
				else
				{
					double c10 = Kh2*log10(h2/10.0);
					corrh2 = c10*log10(d_km/dh2)/log10(d10/dh2);
				}
			}
		}
	}
	return corrh2;
}

double CP154606_Functions::P1546_6(double f_MHz,double t,char z,double h1,double d_km,double h2,int ENV,double tetamax15,int srv,double L,double *Ebfs) 
{
	double E0fs,Eb;
	Eb = P1546_5(f_MHz,t,z,h1,d_km,tetamax15,&E0fs) + Correction_h2(f_MHz, z, h1, d_km, h2, ENV); 
	Eb = min(Eb,E0fs);
	*Ebfs = E0fs;

	if(L!=50)
	{
		if ((z==_T('1'))||(z==_T('2'))||(z==_T('3'))||(z==_T('D')))	//Land
		{
			double K,S,sigmaL1,EsigmaL;
			K = 5.1*(srv==0) + 5.5*(srv==1) + 2.1*(srv==2) + 3.8*(srv==3);
			S = 1.6*(srv==0) + 0.0*(srv==1) + 1.6*(srv==2) + 1.6*(srv==3);

			sigmaL1 = K + S * log10(f_MHz);
			EsigmaL = Qi(L) * sigmaL1;

			Eb = Eb +  EsigmaL;
			Eb = min(Eb,E0fs);
		}
	}
	return Eb;
}

double CP154606_Functions::P1546_5(double f,double t,char z,double h1,double d_km,double tetamax15,double *Ebfs) 
{
	double E0fs,Eb;
	if ((f==100.0)||(f==600.0)||(f==2000.0))	Eb = P1546_4(f,t,z,h1,d_km,tetamax15,&E0fs);
	else
	{
		double finf, fsup , Einf,Efsinf,Esup,Efssup;
		if		(f<600.0)	{	finf = 100.0; fsup = 600.0;		}
		else if (f>600.0)	{	finf = 600.0; fsup = 2000.0;	}
 
		Einf = P1546_4(finf,t,z,h1,d_km,tetamax15,&Efsinf);
		Esup = P1546_4(fsup,t,z,h1,d_km,tetamax15,&Efssup);
		Eb = Einf + (Esup-Einf)*log10(f/finf)/log10(fsup/finf);
		E0fs = Efsinf + (Efssup-Efsinf)*log10(f/finf)/log10(fsup/finf); 
	}
	*Ebfs = E0fs;
	return Eb;
}

double CP154606_Functions::P1546_4(double f,double tp,char z,double h1,double d_km,double tetamax15,double *Ebfs) 
{
	double E0fs,Eb, t = tp;
	t = min(50.0,max(1.0,t));

	if ((t==1.0) || (t==10.0) || (t==50.0) ) 	Eb = P1546_3(f,t,z,h1,d_km,tetamax15,&E0fs);
	else
	{
		double tinf, tsup , Qt,Qinf,Qsup,Einf,Efsinf,Esup,Efssup;
		if		( (t>1.0 ) && (t<10.0) )	{	tinf = 1.0;  tsup = 10.0;	}
		else if ( (t>10.0) && (t<50.0) )	{	tinf = 10.0; tsup = 50.0;	}

		Qt = Qi(t);		Qinf = Qi(tinf);	Qsup = Qi(tsup);
		Einf = P1546_3(f,tinf,z,h1,d_km,tetamax15,&Efsinf);
		Esup = P1546_3(f,tsup,z,h1,d_km,tetamax15,&Efssup);

		Eb = (Esup*(Qinf-Qt)/(Qinf-Qsup)) + (Einf*(Qt-Qsup)/(Qinf-Qsup));
		E0fs = (Efssup*(Qinf-Qt)/(Qinf-Qsup)) + (Efsinf*(Qt-Qsup)/(Qinf-Qsup));
	}
	*Ebfs = E0fs;
	return Eb;
}

double CP154606_Functions::Qi(double xp)
{
	if(xp==50)	return 0;
	double x = xp;
	x = min(100.0-0.0000001,max(0.0000001,x));
	x = x/100.0;

	double  c0 = 2.515517,		c1 = 0.802853,		c2 = 0.010328,
			d1 = 1.432788,		d2 = 0.189269,		d3 = 0.001308;
	double T,P1,P2,P,Qi;

	if ( x<=0.5 )
	{
		T = sqrt(-2.0*log(x));
		P1 = ((c2*T+c1)*T)+c0;
		P2 = ((d3*T+d2)*T+d1)*T+1.0;
		P = P1/P2;
		Qi = T-P;
	}
	else
	{
		T = sqrt(-2.0*log(1.0-x));
		P1 = ((c2*T+c1)*T)+c0;
		P2 = ((d3*T+d2)*T+d1)*T+1.0;
		P = P1/P2;
		Qi = -(T-P);
	}
	return Qi;
}

double CP154606_Functions::P1546_3(double f,double t,char z,double h1,double d_km,double tetamax15,double *Ebfs) 
{
	double E0fs,Eb;

	if ((z==_T('1'))||(z==_T('2'))||(z==_T('3'))||(z==_T('D')))	//Land
	{
		if (h1<0.0) 	Eb = P1546_2(f,t,z,0.0,d_km,&E0fs) + TCAcorr1546(tetamax15,f,d_km);
		else			Eb = P1546_2(f,t,z,h1,d_km,&E0fs);
	}
	else 
	{
		if (h1<1.0) 	Eb = P1546_2(f,t,z,1.0,d_km,&E0fs);
		else			Eb = P1546_2(f,t,z,h1,d_km,&E0fs);
	} 

	*Ebfs = E0fs;
	return Eb;
}

double CP154606_Functions::TCAcorr1546(double Tdegp,double f_MHz,double d_km) 
{
	double Tdeg = min(max(Tdegp , 0.55) , 40.0);

	double v = 0.065 * Tdeg * sqrt(f_MHz);
	double v01 = v - 0.1;
	double Jv = 6.9 + 20.0*log10(sqrt(1.0 + v01*v01) + v01);
	double vp = 0.036 * sqrt(f_MHz);
	double vp01 = vp - 0.1;
	double Jvp = 6.9 + 20.0*log10(sqrt(1.0 + vp01*vp01) + vp01);
	double Corr = Jvp - Jv;

	double pi = 4.0*atan(1.0);
	double k = 4.0/3.0;

	double Te = (180.0*d_km)/(6370.0*pi*k); 
	double Corr_tropo = 30.0*log10(Te/(Te+Tdeg));
	Corr = max(Corr , Corr_tropo);

	return Corr;
}


double CP154606_Functions::P1546_2(double f,double t,char z,double h1,double d_km,double *Ebfs) 
{
	double E0fs,Eb,dum;
	dum = P1546_0(f,t,z,10.0,d_km,&E0fs);
	*Ebfs = E0fs;

	if ( (h1>=10.0) && (h1<=1200.0) )
		Eb = P1546_1(f,t,z,h1,d_km,&dum);
	else if (h1>1200.0)
		Eb = P1546_0(f,t,z,600.0,d_km,&dum)+(P1546_0(f,t,z,1200.0,d_km,&dum)-P1546_0(f,t,z,600.0,d_km,&dum))*log10(h1/600.0)/log10(2.0);
	else if ( (h1>=0.0) && (h1<10.0) )
	{
		if ((z==_T('1'))||(z==_T('2'))||(z==_T('3'))||(z==_T('D')))	//Land
			Eb = P1546_1Land(f,t,z,h1,d_km);
		else
			Eb = P1546_1Sea(f,t,z,h1,d_km);
	}
	Eb = min(Eb,E0fs);
	return Eb;
}


double CP154606_Functions::P1546_1Sea(double f,double t,char z,double h1p,double d_km) 
{
	double h1 = max(1.0,h1p);
	double E0fs,Eb;

	double Dh1 = zD06(f,h1,10.0);
	double D20 = zD06(f,20.0,10.0);

	if (d_km<=Dh1)
		E0fs = P1546_1(f,t,z,10.0,d_km,&Eb);
	else if ( (d_km>Dh1) && (d_km<D20) )
	{	
		double EDh1;
		E0fs = P1546_0(f,t,z,10.0,Dh1,&EDh1);
		double ED20 = P1546_0(f,t,z,10.0,D20,&E0fs) + (P1546_0(f,t,z,20.0,D20,&E0fs) - P1546_0(f,t,z,10.0,D20,&E0fs))*log10(h1/10.0)/log10(2.0);
		Eb = EDh1 + (ED20 - EDh1) * log10(d_km/Dh1) / log10(D20/Dh1);
	}
	else if (d_km>=D20)
	{
		double Ep = P1546_0(f,t,z,10.0,d_km,&E0fs)+(P1546_0(f,t,z,20.0,d_km,&E0fs) - P1546_0(f,t,z,10.0,d_km,&E0fs)) * log10(h1/10.0) / log10(2.0);
		double Epp = P1546_1Land(f,t,z,h1,d_km);
		double Fs = (d_km - D20) / d_km;
		Eb = Ep * (1.0-Fs) + Epp * Fs;
	}
	return Eb;
}

double CP154606_Functions::P1546_1Land(double f,double t,char z,double h1,double d_km) 
{
	double ds = 4.1*sqrt(h1);
	double d0 = 4.1*sqrt(10.0);
	double E0fs,Eb;

	if (d_km<ds)
		Eb = P1546_0(f,t,z,10.0,d0,&E0fs) + P1546_0(f,t,z,10.0,d_km,&E0fs) - P1546_0(f,t,z,10.0,ds,&E0fs);
	else
		Eb = P1546_0(f,t,z,10.0, (d_km + d0 - ds),&E0fs);

	return Eb;
}

double CP154606_Functions::zD06(double f,double h1p,double h2) 
{
	double h1 = max(0.0,h1p);
	double Df = 0.0000389*f*h1*h2;
	double Dh = 4.1*(sqrt(h1)+sqrt(h2));
	double y = Df*Dh/(Df+Dh);
	y = max(0.001,y);
	return y;
}

double CP154606_Functions::P1546_1(double f,double t,char z,double h1,double d_km,double *Ebfs) 
{
	double Eb,E0fs;

	if ((h1==10.0)||(h1==20.0)||(h1==37.5)||(h1==75.0)||(h1==150.0)||(h1==300.0)||(h1==600.0)||(h1==1200.0))
		Eb = P1546_0( f , t , z , h1 , d_km , &E0fs );
	else
	{
		double h11,h12,E1,E2;

		int h1flag = 1*( (h1 > 10.0  ) && (h1 < 20.0  ) ) +
					 2*( (h1 > 20.0  ) && (h1 < 37.5  ) ) +
					 3*( (h1 > 37.5  ) && (h1 < 75.0  ) ) +
					 4*( (h1 > 75.0  ) && (h1 < 150.0 ) ) +
					 5*( (h1 > 150.0 ) && (h1 < 300.0 ) ) +
					 6*( (h1 > 300.0 ) && (h1 < 600.0 ) ) +
					 7*( (h1 > 600.0 ) && (h1 < 1200.0) );
		switch (h1flag)
		{
			case 1:{h11 = 10.0;		h12 = 20.0;  }break;
			case 2:{h11 = 20.0;		h12 = 37.5;  }break;
			case 3:{h11 = 37.5;		h12 = 75.0;  }break;
			case 4:{h11 = 75.0;		h12 = 150.0; }break;
			case 5:{h11 = 150.0;	h12 = 300.0; }break;
			case 6:{h11 = 300.0;	h12 = 600.0; }break;
			case 7:{h11 = 600.0;	h12 = 1200.0;}break;
		}//h1flag

		E1 = P1546_0( f , t , z , h11 , d_km , &E0fs );
		E2 = P1546_0( f , t , z , h12 , d_km , &E0fs );
		Eb = E1+(E2-E1)*log10(h1/h11)/log10(h12/h11);
	}
	*Ebfs = E0fs;
	return Eb;
}

