void CP154606_Functions::reckon(double lat_deg,double lon_deg,double rng_km,double az_deg,double *latC_deg,double *lonC_deg) 
{
	double pi=4.0*atan(1.0);
	float	lat_rad = (float)(lat_deg*pi/180.0),
			lon_rad = (float)(lon_deg*pi/180.0),
			RLON, RLAT,	 rng_km1 = (float)rng_km, az_deg1 = (float)az_deg;
	GEOPDAP( &lon_rad, &lat_rad, &rng_km1, &az_deg1, &RLON, &RLAT);
	*latC_deg = RLAT*180.0/pi;
	*lonC_deg = RLON*180.0/pi;
}


CString CP154606_Functions::GE06_STN(CString Fragment,CString NoticeType) 
{
	CString STN = _T("");
	if((Fragment==_T("RC06"))||(Fragment==_T("GE06D")))
	{
		if		((NoticeType==_T("DS1"))||(NoticeType==_T("GS1")))	STN = _T("ASS");
		else if ((NoticeType==_T("DT1"))||(NoticeType==_T("GT1")))	STN = _T("AST");
		else if ((NoticeType==_T("DS2"))||(NoticeType==_T("GS2")))	STN = _T("ALS");
		else if ((NoticeType==_T("DT2"))||(NoticeType==_T("GT2")))	STN = _T("ALT");
	}
	else if(Fragment==_T("GE06A"))									STN = _T("ASA");
	else if(Fragment==_T("GE06L"))									STN = _T("FXM");

	return STN;
}


CString CP154606_Functions::GE06_Band(double frq_MHz) 
{
	CString Band = _T("");
	if     ((frq_MHz>=170)&&(frq_MHz<eps+230))	Band = _T("III");
	else if((frq_MHz>=470)&&(frq_MHz<eps+582))	Band = _T("IV");
	else if((frq_MHz >582)&&(frq_MHz<eps+718))	Band = _T("VL");
	else if((frq_MHz >718)&&(frq_MHz<eps+862))	Band = _T("VH");
	return Band;
}

double CP154606_Functions::GE06_FStrig(CString STN, double frq_MHz) 
{
	double FStrig;
	CString Band = GE06_Band(frq_MHz) ;

	if((STN==_T("AST"))||(STN==_T("ALT")))
	{
		if     (Band == _T("III"))	FStrig = 17;
		else if(Band ==  _T("IV"))	FStrig = 21;
		else if(Band ==  _T("VL"))	FStrig = 23;
		else if(Band ==  _T("VH"))	FStrig = 25;
	}
	else if((STN==_T("ASS"))||(STN==_T("ALS")))
	{
		if     (Band == _T("III"))	FStrig = 12;
	}
	else if(STN==_T("ASA"))
	{
		if     (Band == _T("III"))	FStrig = 10;
		else if(Band ==  _T("IV"))	FStrig = 18;
		else if(Band ==  _T("VL"))	FStrig = 20;
		else if(Band ==  _T("VH"))	FStrig = 22;
	}
	return FStrig;
}


