int CP154606_Functions::AP3_OVERLAP(double Freq_BS, double BandW_BS, double Freq_ter, double BandW_ter) 
{
	int OVERLAP = 0;
	double BO = 0;
	double freqmin1 = Freq_BS - BandW_BS/2.0;
	double freqmax1 = Freq_BS + BandW_BS/2.0;

	double freqmin2 = Freq_ter - BandW_ter/2.0;
	double freqmax2 = Freq_ter + BandW_ter/2.0;

	if     ((freqmax2>freqmax1)&&(freqmin1<eps+freqmin2)&&(freqmin2<eps+freqmax1))
		BO = freqmax1 - freqmin2;
	else if((freqmin2<freqmin1)&&(freqmin1<eps+freqmax2)&&(freqmax2<eps+freqmax1))
		BO = freqmax2 - freqmin1;
	else if((freqmin2<eps+freqmax1)&&(freqmax1<eps+freqmax2)&&(freqmin2<eps+freqmin1)&&(freqmin1<eps+freqmax2))
		BO = freqmax1 - freqmin1;
	else if((freqmin2<eps+freqmax2)&&(freqmax2<eps+freqmax1)&&(freqmin1<eps+freqmin2)&&(freqmin2<eps+freqmax1))
		BO = freqmax2 - freqmin2;
	if(BO>0)	OVERLAP = 1;
	return OVERLAP;
}

