long CP154606_Functions::Round(double x) 
{
	long y;
	long x1 = (int) x;
	if(x>=0)
	{
		long x2 = x1+1;
		y = ( (x > ((double)x1)+0.45) ? x2 : x1);
	}
	else
	{
		long x2 = x1-1;
		y = ( (x > ((double)x2)+0.45) ? x1 : x2);
	}
	return y;
}

void CP154606_Functions::Sort_A2Z(double D0[],double E0[],long num) 
{
	double temp;
	for(long i = 0;i<num-1;i++)
	{
		for(long j = i+1;j<num;j++)
		{
			if(D0[j]<D0[i])
			{
				temp = D0[i];	D0[i] = D0[j];	D0[j] = temp;
				temp = E0[i];	E0[i] = E0[j];	E0[j] = temp;
			}//if
		}//j
	}//i
}


