double CP154606_Functions::InterpT(double *D0, double *E0, double d, long num, int LinLog)
{
	double Eb;
	long N ,n1 , n2 , Nm;
	n1 = 0;
	n2 = num - 1;
	N = n2 - n1 + 1;

	if(D0[0] < D0[1])
	{
		if	   (d > D0[num-1])	{n1 = num-2;	n2 = num-1;	}
		else if(d < D0[0])		{n1 = 1;		n2 = 0;		}
		else
		{
			while(N > 2)
			{
				Nm = (n2 - n1)/2 + n1;
				if	   (d > D0[Nm])		n1 = Nm;
				else if(d < D0[Nm])		n2 = Nm;
				else					return E0[Nm];
				N = n2 - n1 + 1;
			}
		}
	}
	else
	{
		if	   (d < D0[num-1])	{n1 = num-2;	n2 = num-1;	}
		else if(d > D0[0])		{n1 = 1;		n2 = 0;		}
		else
		{
			while(N > 2)
			{
				Nm = (n2 - n1)/2 + n1;
				if	   (d < D0[Nm])		n1 = Nm;
				else if(d > D0[Nm])		n2 = Nm;
				else					return E0[Nm];
				N = n2 - n1 + 1;
			}
		}
	}

	if(LinLog==0)	Eb = E0[n1] + (E0[n2] - E0[n1])*(d - D0[n1])/(D0[n2] - D0[n1]);
	else			Eb = E0[n1] + (E0[n2] - E0[n1])*log10(d/D0[n1])/log10(D0[n2]/D0[n1]);
	return Eb;
}


