double CP154606_Functions::Emission2NBW(CString Emission) 
{
	double NBW_kHz = 0;
	CString c , BWs = Emission.Left(4);
	BWs.MakeUpper();

	for(int i=0;i<4;i++)
	{
		c = BWs.Mid(i,1);
		if(c=="H")
		{
			BWs.Replace('H','.');
			NBW_kHz = (0.001)*atof(BWs);
			return NBW_kHz;
		}
		else if(c=="K")
		{
			BWs.Replace('K','.');
			NBW_kHz = atof(BWs);
			return NBW_kHz;
		}
		else if(c=="M")
		{
			BWs.Replace('M','.');
			NBW_kHz = (1000.0)*atof(BWs);
			return NBW_kHz;
		}
		else if(c=="G")
		{
			BWs.Replace('G','.');
			NBW_kHz = (1000000.0)*atof(BWs);
			return NBW_kHz;
		}
	}
	return NBW_kHz;
}
