double CP154606_Functions::F_3_1_BT419(double dAz,double Fx_MHz) 
{
	double x1,y1,x2,y2 , y;

	if((Fx_MHz>=170.0)&&(Fx_MHz<=230.0))	//Band III
	{
		x1 = 27;	y1 = 0;
		x2 = 60;	y2 = -12.0;
	}
	else if((Fx_MHz>=470.0)&&(Fx_MHz<=862.0))	//Band IV,V
	{
		x1 = 20;	y1 = 0;
		x2 = 60;	y2 = -16.0;
	}

	y = y1+(dAz-x1)*(y2-y1)/(x2-x1);

//	if(dAz<x2)	y = y1+(dAz-x1)*(y2-y1)/(x2-x1);
//	else		y = y2;

	y = fabs( max(min(y1,y),y2) );
	return y;
}


