double CP154606_Functions::Table_AP4_2_45(CString FXM,double Freq_I,double Freq_W,double B_I,double B_W,CString MASK_I,
										  CString Fragment_I, CString NoticeType_I, CString TV_COLOR_I, CString TV_SYS_I,
										  double freq_vcarr_I,double freq_scarr_I) 
{
	double A,K;
	if(FXM = _T("FX"))
	{
		if((Freq_I>=174)&&(Freq_I<=230))			A = 1;
		else if((Freq_I>=470)&&(Freq_I<=862))		A = -4.0 - 10.0*log10(Freq_I/500.0);
	}
	else if(FXM = _T("FB"))
	{
		if((Freq_I>=174)&&(Freq_I<=230))			A = 5.0 - 16.5*log10(Freq_I/174.0);
		else if((Freq_I>=470)&&(Freq_I<=790))		A = -6.0 - 17.736*log10(Freq_I/470.0);
		else if((Freq_I>=790)&&(Freq_I<=862))		A = -10.0;
	}
	else if(FXM = _T("ML"))
	{
		if((Freq_I>=174)&&(Freq_I<=230))			A = 12.0;
		else if((Freq_I>=470)&&(Freq_I<=862))		A =  7.0;
	}
	double deltaF = Freq_W - Freq_I;
	double Bo = min(B_W, (B_W + B_I)/2.0 - deltaF); 

	double RPR = 0;
	if((NoticeType_I==_T("DT1"))||(NoticeType_I==_T("GT1"))||(NoticeType_I==_T("DT2"))||(NoticeType_I==_T("GT2")))
	{
		if(MASK_I == _T("S"))
		{	
			if(Bo >= B_W)								K = 0;
			else if((Bo>=(0.00001*B_W))&&(Bo<B_W))		K = 10.0*log10(Bo/B_W);
			else if((Bo>=-0.5)&&(Bo<(0.00001*B_W)))		K = -50;
			else if(Bo == -1)							K = -55;
			else if(Bo == -2)							K = -62;
			else if(Bo == -4)							K = -70;
			else if(Bo <= -8)							K = -87;
			else
			{
				double x1,x2,y1,y2;
				if((Bo > -8)&&(Bo<-4))
				{
					x1 = -8;    x2 = -4;		y1 = -87;    y2 = -70;
				}
				else if((Bo > -4)&&(Bo<-2))
				{
					x1 = -4;    x2 = -2;		y1 = -70;    y2 = -62;
				}
				else if((Bo > -2)&&(Bo<-1))
				{
					x1 = -2;    x2 = -1;		y1 = -62;    y2 = -55;
				}
				else if((Bo > -1)&&(Bo<-0.5))
				{
					x1 = -1;    x2 = -0.5;		y1 = -55;    y2 = -50;
				}
				K = y1+(y2-y1)*(Bo-x1)/(x2-x1);
			}
		}
		else //if(MASK_I == _T("N"))
		{
			if(Bo >= B_W)								K = 0;
			else if((Bo>=(0.0001*B_W))&&(Bo<B_W))		K = 10.0*log10(Bo/B_W);
			else if((Bo>=-0.5)&&(Bo<(0.0001*B_W)))		K = -40;
			else if(Bo == -1)							K = -45;
			else if(Bo == -2)							K = -52;
			else if(Bo == -4)							K = -60;
			else if(Bo <= -8)							K = -77;
			else
			{
				double x1,x2,y1,y2;
				if((Bo > -8)&&(Bo<-4))
				{
					x1 = -8;    x2 = -4;		y1 = -77;    y2 = -60;
				}
				else if((Bo > -4)&&(Bo<-2))
				{
					x1 = -4;    x2 = -2;		y1 = -60;    y2 = -52;
				}
				else if((Bo > -2)&&(Bo<-1))
				{
					x1 = -2;    x2 = -1;		y1 = -52;    y2 = -45;
				}
				else if((Bo > -1)&&(Bo<-0.5))
				{
					x1 = -1;    x2 = -0.5;		y1 = -45;    y2 = -40;
				}
				K = y1+(y2-y1)*(Bo-x1)/(x2-x1);
			}

		}
		RPR = 0;
	}
	else if(Fragment_I==_T("GE06A"))
	{
		K = 0;
		RPR = ATV2FXM_RPR(Freq_W, B_W, FXM,freq_vcarr_I, freq_scarr_I, TV_COLOR_I, TV_SYS_I); 
	}

	double E = -37.0 + A + 10.0*log10(B_I) + 20.0*log10(Freq_I) - K - RPR;
	return E;
}



double CP154606_Functions::ATV2FXM_RPR(double Freq_W,double Band_W,CString sttp_W, 
							double freq_vcarr_I,double freq_scarr_I,CString TV_COLOR_I,CString TV_SYS_I) 
{
	double RPR = 0;	// NA

	double Freqvis_I = freq_vcarr_I;
	double Freqs_I   = freq_scarr_I;
	double DELTAF = Freqvis_I - Freq_W;
	double DELTAFS = Freqs_I - Freq_W;

	if(TV_COLOR_I.GetLength()==0)
	{
		if(TV_SYS_I == _T("L"))		TV_COLOR_I = _T("SECAM");
		else						TV_COLOR_I = _T("PAL");
	}

	double Freq_Color;
	if(TV_SYS_I == _T("M"))											Freq_Color = 3.575 + Freqvis_I;
	else if((TV_SYS_I != _T("M"))&&(TV_COLOR_I == _T("PAL")))		Freq_Color = 4.433 + Freqvis_I;
	else if((TV_SYS_I != _T("M"))&&(TV_COLOR_I == _T("SECAM")))		Freq_Color = 4.406 + Freqvis_I;

	double Freq_Nicam;
	if(((TV_SYS_I != _T("M"))&&(TV_SYS_I != _T("G"))&&(TV_SYS_I != _T("I1")))&&(Freqvis_I <300))	Freq_Nicam = 5.85 + Freqvis_I;
	else if((TV_SYS_I == _T("G"))&&(Freqvis_I <300))												Freq_Nicam = 5.50 + Freqvis_I;
	else if((TV_SYS_I != _T("I"))&&(TV_SYS_I != _T("I1"))&&(Freqvis_I >300))						Freq_Nicam = 5.85 + Freqvis_I;
	else if((TV_SYS_I == _T("I"))||(TV_SYS_I == _T("I1")))											Freq_Nicam = 6.55 + Freqvis_I;

	double Delta_Nicam = Freq_Nicam - Freq_W;
	double Delta_Color = Freq_Color - Freq_W;

	if((sttp_W==_T("FB"))||(sttp_W==_T("ML")))
	{
		if((DELTAF>-eps-0.5)&&(DELTAF<eps+0.5))											RPR = 0;
		else if((DELTAFS>-eps-0.2)&&(DELTAFS<eps+0.2))									RPR = -10;
		else if((Delta_Color>-eps-0.5)&&(Delta_Color<eps+0.5))							RPR = -20;
		else if((Delta_Nicam>-eps-0.25)&&(Delta_Nicam<eps+0.25)&&(TV_SYS_I!=_T("M")))	RPR = -15;
		else																			RPR = -30;
	}
	else if(sttp_W==_T("FX"))
	{
		double X,Y,Z;
		X = (Band_W<eps+1.0) ? 1.0 : Band_W;
		Y = (Band_W<eps+0.4) ? 0.4 : Band_W;
		Z = (Band_W<eps+0.5) ? 0.5 : Band_W;

		if((DELTAF>-eps+(-X/2.0))&&(DELTAF<eps+(X/2.0)))										RPR = 0;
		else if((DELTAFS>-eps+(-Y/2.0))&&(DELTAFS<eps+(Y/2.0)))									RPR = -10;
		else if((Delta_Color>-eps+(-X/2.0))&&(Delta_Color<eps+(X/2.0)))							RPR = -20;
		else if((Delta_Color>-eps+(-Z/2.0))&&(Delta_Color<eps+(Z/2.0))&&(TV_SYS_I!=_T("M")))	RPR = -20;
		else																					RPR = -30;
	}
	return RPR;
}

double CP154606_Functions::InterpT(float *D0, float *E0, double d, long num, int LinLog)
{
	double Eb;
	long N ,n1 , n2 , Nm;
	n1 = 0;
	n2 = num - 1;
	N = n2 - n1 + 1;

	if(D0[0] < D0[1])
	{
		if	   (d > D0[num-1])	{n1 = num-2;	n2 = num-1;	}
		else if(d < D0[0])		{n1 = 1;		n2 = 0;		}
		else
		{
			while(N > 2)
			{
				Nm = (n2 - n1)/2 + n1;
				if	   (d > D0[Nm])		n1 = Nm;
				else if(d < D0[Nm])		n2 = Nm;
				else					return E0[Nm];
				N = n2 - n1 + 1;
			}
		}
	}
	else
	{
		if	   (d < D0[num-1])	{n1 = num-2;	n2 = num-1;	}
		else if(d > D0[0])		{n1 = 1;		n2 = 0;		}
		else
		{
			while(N > 2)
			{
				Nm = (n2 - n1)/2 + n1;
				if	   (d < D0[Nm])		n1 = Nm;
				else if(d > D0[Nm])		n2 = Nm;
				else					return E0[Nm];
				N = n2 - n1 + 1;
			}
		}
	}

	if(LinLog==0)	Eb = E0[n1] + (E0[n2] - E0[n1])*(d - D0[n1])/(D0[n2] - D0[n1]);
	else			Eb = E0[n1] + (E0[n2] - E0[n1])*log10(d/D0[n1])/log10(D0[n2]/D0[n1]);
	return Eb;
}

















